import React from 'react'
import ReactDOM from 'react-dom'
import Backdrop from './Backdrop'
import './Modal.css'
import { CSSTransition } from 'react-transition-group';


const ModalOverlay = props => {
    const content = (
        <div className={`modal ${props.className}`} style={props.style}>
            <header className={`modal__header ${props.headerClassName}`}>
                <h2>{props.header}</h2>
            </header>

            <form onSubmit={props.onSubmit? props.onSubmit : event => event.preventDefault()}>
                <div className={`modal__content ${props.contentClass}`} ></div>
            </form>

            <footer className={`modal__footer ${props.footerClass}`}>
                {props.footer}
            </footer>
        </div>
    )

    return(
        ReactDOM.createPortal(content, document.getElementById('modal-hook'))
    )
}

const Modal = props => {
  return (
    <>
        {props.show && <Backdrop onclick={props.onCancel}/>}
        <CSSTransition
        in={props.show}
        mountOnEnter
        unmountOnExit
        timeout={200}
        classNames="modal"> <ModalOverlay {...props}/></CSSTransition>
    </>
  )
}

export default Modal
