import React from 'react'
import ReactDOM from 'react-dom'
import Backdrop from './Backdrop'
import './Modal.css'

const ModalOverlay = propss => {
    const content = (
        <div className={`modal ${props.className}`} style={propss.style}>
            <header className={`modal__header ${props.headerClassName}`}>
                <h2>{propss.header}</h2>
            </header>

            <form onSubmit={propss.onSubmit? propss.onSubmit : event => event.preventDefault()}>
                <div className={`modal__content ${propss.contentClass}`} ></div>
            </form>

            <footer className={`modal__footer ${propss.footerClass}`}>
                {propss.footer}
            </footer>
        </div>
    )

    return(
        ReactDOM.createPortal(content, document.getElementById('modal-hook'))
    )
}

const Modal = propss => {
  return (
    <>
        {propss.show && <Backdrop onclick={propss.onCancel}/>}
        <ModalOverlay {...propss}/>
    </>
  )
}

export default Modal
